INSERT INTO "public"."Games" ("game_id", "title", "description", "setting", "price", "is_active", "subtitle", "image", "difficulty", "players", "duration", "featured", "title_bg", "subtitle_bg", "description_bg", "difficulty_bg", "victim", "victim_bg", "setting_bg", "image_path") VALUES
	(5, 'Turbulence: Her Last One', 'A luxury international flight aboard Orion Air 777, mid-flight between Istanbul and New York, becomes the scene of a mysterious murder when Chief Flight Attendant Zaynep Arslan is found dead in the crew quarters. With the plane at cruising altitude and no way for the killer to escape, the murderer must be among the passengers or crew. But who had the motive, means, and opportunity to commit this carefully planned crime?', 'A luxury international flight aboard Orion Air 777, mid-flight between Istanbul and New York', 14.99, true, 'A High-Altitude Mystery', 'null', 'Medium', '4-8', '2-3 hours', false, 'Турбуленция: Последният ѝ полет', 'Мистерия на висока надморска височина', 'Луксозен международен полет с Orion Air 777, става сцена на мистериозно убийство, когато главната стюардеса Зейнеп Арслан е намерена мъртва в помещението на екипажа. Намирайки се на 10 000 метра надморска височина и без възможност убиецът да избяга, той трябва да е сред пътниците и екипажа. Но кой е имал мотив, средства и възможност да извърши това внимателно планирано престъпление?', 'Средна', 'Zaynep Arslan', 'Зейнеп Арслан', 'Луксозен международен полет с Orion Air 777, в средата на пътуването между Истанбул и Ню Йорк', '5/turbulence.jpg');
