INSERT INTO "public"."Characters" ("character_id", "variation_id", "name", "description", "secret", "name_bg", "description_bg", "secret_bg", "connection", "connection_bg", "circumstances", "circumstances_bg") VALUES
	(1016, NULL, 'Dr. Felix Moreau', 'Celebrity Psychiatrist', 'Lost his license for unauthorized sedative trials on patients', 'Д-р Феликс Моро', 'Известен психиатър', 'Загуби лиценза си за неоторизирани опити с успокоителни върху пациенти', 'Zaynep previously requested his help with a disturbed passenger; Felix refused, fearing she might recognize a case tied to his scandal', 'Зейнеп поиска помощта му за разстроен пътник; Феликс отказа, страхувайки се, че тя може да го разпознае и свърже с активен медиен скандал, който върви в новините', 'Carried a medical bag onboard. Claims to have been asleep in his seat during the time of death', 'Носеше медицинска чанта на борда. Твърди, че е спал на седалката си по време на убийството'),
	(1014, NULL, 'Ayla Karaca', 'Junior Flight Attendant', 'Caught stealing expensive liquor; Zaynep had warned her this was her final chance', 'Айла Караджа', 'Младша стюардеса', 'Хваната да краде скъп алкохол; Зейнеп я предупреди, че това е последното ѝ предупреждение', 'Zaynep had recently blocked her promotion citing "trust concerns"', 'Зейнеп наскоро блокира повишението ѝ, като посочи "не може да ѝ се има доверие"', 'Seen near the crew quarters minutes before the murder. Locker found open after the body was discovered', 'Видяна близо до помещението на екипажа минути преди убийството. Шкафчето ѝ е намерено отворено веднага след извършване на престъплението'),
	(1015, NULL, 'Marco Salvi', 'Italian Businessman', 'Smuggling uncut diamonds using diplomatic luggage', 'Марко Салви', 'Италиански бизнесмен', 'Контрабандира с необработени диаманти, използвайки дипломатически багаж', 'Zaynep discovered a suspicious pouch in his bag, later seen returning it', 'Зейнеп откри подозрителна торбичка в багажа му, но по-късно е видяна да я връща', 'Claimed Zaynep "accepted his apology," though the exact nature of their exchange is unclear. Reported a camera glitch hours before the murder', 'Твърди, че Зейнеп "е приела извинението му," въпреки че точният характер на разговора им не е ясен. Докладва проблем с камерата часове преди убийството'),
	(1013, NULL, 'Talia Monroe', 'Social Media Influencer', 'Faking a serious illness to receive sympathy donations', 'Талия Монро', 'Инфлуенсър в социални мрежи', 'Фалшифицира сериозна болест, за да получава дарения от съчувствие', 'Zaynep overheard her staging a phone call about her fake condition', 'Зейнеп дочу как тя прави телефонно обаждане относно престореното ѝ състояние', 'Deleted the raw footage of her livestream. Claims she was editing in the lavatory during the murder', 'Изтрила е необработени кадри от нейния лайв-стрийм. Твърди, че е редактирала в тоалетната по време на убийството'),
	(1017, NULL, 'Omar Nasser', 'Airline Engineer on Emergency Transfer', 'Suspended for breaching safety protocols; his presence on this flight was last-minute', 'Омар Насер', 'Авиоинженер, спешен трансфер', 'Отстранен за нарушаване на протоколи за сигурност; присъствието му на този полет е записано в последния момент', 'Zaynep recognized him from a previous mechanical failure incident and warned the captain', 'Зейнеп го разпозна от предишен инцидент с механичен проблем на борда и предупреди капитана', 'Had access to crew systems and electrical panels; was seen near the rear maintenance panel shortly before the camera blackout', 'Имаше достъп до системите на екипажа и електрическите панели; видян бе близо до задния поддържащ панел малко преди изключването на камерите'),
	(1018, NULL, 'Ingrid Weiss', 'Retired Interpol Agent', 'She is not retired — she is covertly investigating a trafficking route tied to high-end smuggling rings, and this flight was her lead', 'Ингрид Вайс', 'Пенсиониран агент на Интерпол', 'Тя не е пенсионирана — тайно разследва трафик маршрут, свързан с контрабандни сделки на скъпи артефакти. Следа от разследването я доведе на борда на самолета', 'Zaynep once helped reroute a passenger off a previous flight at Ingrid''s request, but never asked why', 'Зейнеп веднъж помогна да пренасочи пътник от предишен полет по молба на Ингрид, но никога не попита защо', 'Was seen entering the cockpit area under the pretense of checking turbulence protocols. Claimed to have been asleep in business class at time of death', 'Видяна е да влиза в зоната на пилотската кабина под претекст да проверява протоколите за турбуленция. Твърди, че е спала в бизнес класа по време на убийството'),
	(1019, NULL, 'Devon Reyes', 'In-Flight Chef Trainee', 'Was paid off to spike a passenger''s drink on a previous flight — an incident Zaynep suspected but never proved', 'Девън Рийс', 'Стажант готвач на борда', 'Беше подкупен да предложи напитка на пътник от предишен полет — инцидент, който Зейнеп подозираше, но никога не доказа', 'Zaynep had submitted an official complaint questioning Devon''s conduct around the galley after observing an unusual drink prep', 'Зейнеп подаде официална жалба, поставяйки под въпрос поведението на Девън в кухнята след като забеляза как подготвя необичайна напитка', 'Claims to have been preparing meals, but kitchen logs show he left the galley unattended around the time of death', 'Твърди, че е приготвял храна, но кухненските записи показват, че е напуснал кухнята без надзор около времето на смъртта'),
	(1020, NULL, 'Hana Ito', 'Aerospace Systems Analyst (Undercover Audit)', 'Assigned anonymously by Orion Air to investigate suspicious operational behavior and security risks on long-haul flights', 'Хана Ито', 'Аналитик на аерокосмически системи (Под прикритие)', 'Назначена анонимно от Orion Air да разследва подозрително оперативно поведение и рискове за сигурност при дълги полети', 'Zaynep unknowingly cooperated with Hana weeks prior in reporting procedural inconsistencies, unaware Hana was internal audit', 'Зейнеп неволно подпомогна Хана преди седмици, при докладване на процедурни несъответствия, незнаейки че Хана е вътрешен одит', 'Claimed to be reviewing passenger satisfaction reports, but her seat sensor was inactive for 47 minutes around the time of the blackout', 'Твърди, че е преглеждала доклади за удовлетвореност на пътници, но сензорът на седалката ѝ е бил неактивен за 47 минути около времето на изключване на камерите');
