INSERT INTO "public"."Rounds" ("round_id", "variation_id", "round_number", "title", "description", "title_bg", "description_bg") VALUES
	(85, 21, 1, 'Crime Scene Investigation', 'Initial examination of the crew quarters. Review the available clues and discuss what insights you can gather from them, so you can decide which character''s secret you want to reveal in the next round.', 'Разследване на местопрестъплението', 'Първоначално преглеждане на помещенията на екипажа. Разгледайте откритите улики и обсъдете каква информация ви предоставят. Стигнете до заключение чия тайна бихте искали да разкриете през следващия рунд.'),
	(86, 21, 2, 'Secret Revelation', 'Cast your vote to uncover a character''s secret. Once a majority is reached through verbal voting, reveal the chosen character''s secret.', 'Разкриване на тайна', 'Гласувайте, за да разкриете тайната на един от героите. След като се достигне мнозинство чрез вербално гласуване, разкрийте тайната на избрания герой.'),
	(87, 21, 3, 'Deeper Investigation', 'Further examination of evidence and alibis. It''s time to start speculating!', 'По-задълбочено разследване', 'По-задълбочено преглеждане на улики и обсоятелства. Нека спекулациите започнат сега!'),
	(88, 21, 4, 'Secret Revelation', 'Cast your vote to uncover a character''s secret. Once a majority is reached through verbal voting, reveal the chosen character''s secret.', 'Разкриване на тайна', 'Гласувайте, за да разкриете тайната на един от героите. След като се достигне мнозинство чрез вербално гласуване, разкрийте тайната на избрания герой.'),
	(89, 21, 5, 'Final Evidence', 'Connecting all evidence and motives. Time to put your heads together and decide who the murderer is. Required majority of at least 51%.', 'Финални улики', 'Свързване на всички улики и мотиви. Време е да достигнете до заключение кой е убиецът с мнозинство от поне 51%.'),
